server/routes.ts.content:
import type { Express, Request } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertCertificateSchema } from "@shared/schema";

// Extend Express Request type to include session
declare module "express-serve-static-core" {
  interface Request {
    session: {
      userId?: number;
      destroy(callback: (err: any) => void): void;
    };
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Authentication middleware
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session?.userId) {
      return res.status(401).json({ message: "Authentication required" });
    }
    next();
  };

  // Admin login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Username and password required" });
      }

      const user = await storage.getUserByUsername(username);
      if (!user || user.password !== password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      res.json({ message: "Login successful", user: { id: user.id, username: user.username } });
    } catch (error) {
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Admin logout
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err: any) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  // Check auth status
  app.get("/api/auth/me", async (req, res) => {
    if (!req.session?.userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user) {
      return res.status(401).json({ message: "User not found" });
    }

    res.json({ user: { id: user.id, username: user.username } });
  });

  // Public certificate lookup
  app.get("/api/certificates/lookup/:certificateNumber", async (req, res) => {
    try {
      const { certificateNumber } = req.params;

      if (!certificateNumber) {
        return res.status(400).json({ message: "Certificate number is required" });
      }

      const certificate = await storage.getCertificateByNumber(certificateNumber);
      if (!certificate) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json(certificate);
    } catch (error) {
      res.status(500).json({ message: "Search failed" });
    }
  });

  // Admin routes - require authentication
  app.get("/api/certificates", requireAuth, async (req, res) => {
    try {
      const certificates = await storage.getAllCertificates();
      res.json(certificates);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch certificates" });
    }
  });

  app.post("/api/certificates", requireAuth, async (req, res) => {
    try {
      const validatedData = insertCertificateSchema.parse(req.body);

      // Check if certificate number already exists
      const existing = await storage.getCertificateByNumber(validatedData.certificateNumber);
      if (existing) {
        return res.status(400).json({ message: "Certificate number already exists" });
      }

      const certificate = await storage.createCertificate(validatedData);
      res.status(201).json(certificate);
    } catch (error: any) {
      if (error.errors) {
        return res.status(400).json({ message: "Validation failed", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create certificate" });
    }
  });

  app.put("/api/certificates/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertCertificateSchema.partial().parse(req.body);

      const certificate = await storage.updateCertificate(id, validatedData);
      if (!certificate) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json(certificate);
    } catch (error: any) {
      if (error.errors) {
        return res.status(400).json({ message: "Validation failed", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update certificate" });
    }
  });

  app.delete("/api/certificates/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteCertificate(id);

      if (!deleted) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json({ message: "Certificate deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete certificate" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { certificates, type Certificate, type InsertCertificate } from '../shared/schema.js';
import { db } from './db.js';
import { eq, ilike } from 'drizzle-orm';
import { randomUUID } from 'crypto';

export interface IStorage {
  getCertificate(certificateNumber: string): Promise<Certificate | undefined>;
  createCertificate(insertCertificate: InsertCertificate): Promise<Certificate>;
  checkCertificateExists(certificateNumber: string): Promise<boolean>;
}

export class DatabaseStorage implements IStorage {
  async getCertificate(certificateNumber: string): Promise<Certificate | undefined> {
    // Try exact match first
    let [certificate] = await db
      .select()
      .from(certificates)
      .where(eq(certificates.certificateNumber, certificateNumber));

    // If no exact match, try case-insensitive search
    if (!certificate) {
      [certificate] = await db
        .select()
        .from(certificates)
        .where(ilike(certificates.certificateNumber, certificateNumber));
    }

    return certificate || undefined;
  }

  async createCertificate(insertCertificate: InsertCertificate): Promise<Certificate> {
    const [certificate] = await db
      .insert(certificates)
      .values({
        id: randomUUID(),
        ...insertCertificate,
      })
      .returning();
    return certificate;
  }

  async checkCertificateExists(certificateNumber: string): Promise<boolean> {
    // Check both exact and case-insensitive matches
    const [existing] = await db
      .select({ certificateNumber: certificates.certificateNumber })
      .from(certificates)
      .where(ilike(certificates.certificateNumber, certificateNumber))
      .limit(1);
    return !!existing;
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { pgTable, text, varchar, timestamp } from 'drizzle-orm/pg-core';
import { createInsertSchema } from 'drizzle-zod';
import { z } from 'zod';

export const certificates = pgTable('certificates', {
  id: text('id').primaryKey(),
  certificateNumber: varchar('certificate_number', { length: 100 }).unique().notNull(),
  gemstoneType: varchar('gemstone_type', { length: 100 }).notNull(),
  caratWeight: varchar('carat_weight', { length: 50 }).notNull(),
  cut: varchar('cut', { length: 100 }).notNull(),
  measurements: varchar('measurements', { length: 100 }).notNull(),
  clarity: varchar('clarity', { length: 50 }).notNull(),
  color: varchar('color', { length: 50 }).notNull(),
  polish: varchar('polish', { length: 50 }).notNull(),
  origin: varchar('origin', { length: 100 }).notNull(),
  imageUrl: text('image_url'),
  issueDate: timestamp('issue_date').defaultNow(),
  createdAt: timestamp('created_at').defaultNow(),
});

export const insertCertificateSchema = createInsertSchema(certificates).omit({
  id: true,
  issueDate: true,
  createdAt: true,
});

export type Certificate = typeof certificates.$inferSelect;
export type InsertCertificate = z.infer<typeof insertCertificateSchema>;

