import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import CertificateDisplay from "@/components/certificate-display";
import type { Certificate } from "@shared/schema";

export default function CertificateSearch() {
  const [certificateNumber, setCertificateNumber] = useState("");
  const [shouldSearch, setShouldSearch] = useState(false);
  const { toast } = useToast();

  const { data: certificate, isLoading, error } = useQuery<Certificate>({
    queryKey: ["/api/certificates/lookup", certificateNumber],
    enabled: shouldSearch && certificateNumber.trim() !== "",
    retry: false,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!certificateNumber.trim()) {
      toast({
        title: "Error",
        description: "Please enter a certificate number",
        variant: "destructive",
      });
      return;
    }
    setShouldSearch(true);
  };

  const handleReset = () => {
    setCertificateNumber("");
    setShouldSearch(false);
  };

  return (
    <div className="space-y-8">
      {/* Search Form */}
      <div className="max-w-2xl mx-auto">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <Input
                type="text"
                id="certificateNumber"
                value={certificateNumber}
                onChange={(e) => setCertificateNumber(e.target.value)}
                placeholder="Certificate Number"
                className="h-14 px-6 text-lg border-2 border-gray-300 focus:border-primary rounded-lg"
              />
            </div>
            <Button 
              type="submit" 
              className="h-14 px-8 bg-primary hover:bg-primary/90 text-white font-semibold rounded-lg pulse-glow"
              disabled={isLoading}
            >
              {isLoading ? "Searching..." : "Track Report"}
            </Button>
          </div>
          {shouldSearch && (
            <div className="text-center">
              <Button type="button" variant="outline" onClick={handleReset} className="px-6">
                Clear Search
              </Button>
            </div>
          )}
        </form>
      </div>

      {/* Search Results */}
      {error && (
        <div className="max-w-4xl mx-auto">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
            <div className="text-red-600 mb-2">
              <svg className="w-8 h-8 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h3 className="text-lg font-semibold text-red-800 mb-2">Certificate Not Found</h3>
            <p className="text-red-600">
              No certificate found with number "{certificateNumber}". Please verify the certificate number and try again.
            </p>
          </div>
        </div>
      )}

      {certificate && (
        <div className="max-w-4xl mx-auto">
          <CertificateDisplay certificate={certificate} />
        </div>
      )}
    </div>
  );
}

