import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { LogOut, Plus, List, Download, Shield, FileText, Users, Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import AdminLogin from "@/components/admin-login";
import CertificateForm from "@/components/certificate-form";
import type { Certificate } from "@shared/schema";
import gieLogoPath from "@assets/logo gie_1753197610784.png";

export default function Admin() {
  const [user, setUser] = useState<{ id: number; username: string } | null>(null);
  const [showCertificateForm, setShowCertificateForm] = useState(false);
  const { toast } = useToast();

  // Check if user is authenticated
  const { data: authData, isLoading: authLoading } = useQuery({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  // Get all certificates for admin view
  const { data: certificates = [], isLoading: certificatesLoading } = useQuery<Certificate[]>({
    queryKey: ["/api/certificates"],
    enabled: !!user,
  });

  const logoutMutation = useMutation({
    mutationFn: () => apiRequest("POST", "/api/auth/logout"),
    onSuccess: () => {
      setUser(null);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      toast({
        title: "Success",
        description: "Logged out successfully",
      });
    },
  });

  useEffect(() => {
    if (authData?.user) {
      setUser(authData.user);
    }
  }, [authData]);

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const handleCertificateCreated = () => {
    setShowCertificateForm(false);
    queryClient.invalidateQueries({ queryKey: ["/api/certificates"] });
    toast({
      title: "Success",
      description: "Certificate created successfully",
    });
  };

  if (authLoading) {
    return (
      <div className="min-h-screen bg-neutral flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return <AdminLogin onLoginSuccess={setUser} />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-lg border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-18 py-3">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-3">
                <img src={gieLogoPath} alt="GIE LAB" className="h-12 w-12" />
                <div>
                  <h1 className="text-2xl font-bold text-primary">GIE LABS</h1>
                  <p className="text-sm text-gray-600">Laboratory Management System</p>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-sm font-semibold text-gray-900">
                  Dr. {user.username}
                </p>
                <p className="text-xs text-gray-500">Certified Gemologist</p>
              </div>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={handleLogout}
                disabled={logoutMutation.isPending}
                className="flex items-center space-x-2"
              >
                <LogOut className="w-4 h-4" />
                <span>Logout</span>
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Tabs defaultValue="certificates" className="space-y-8">
          <TabsList className="grid w-full grid-cols-2 h-12">
            <TabsTrigger value="certificates" className="flex items-center space-x-2 h-10">
              <FileText className="w-4 h-4" />
              <span>Certificate Database</span>
            </TabsTrigger>
            <TabsTrigger value="add-certificate" className="flex items-center space-x-2 h-10">
              <Plus className="w-4 h-4" />
              <span>Issue Certificate</span>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="certificates" className="space-y-6">
            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <Card className="premium-card">
                <CardHeader className="pb-3">
                  <CardTitle className="text-lg flex items-center">
                    <FileText className="w-5 h-5 mr-2 text-primary" />
                    Total Certificates
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-3xl font-bold text-primary">
                    {certificates.length}
                  </div>
                  <p className="text-sm text-gray-600 mt-1">Issued certificates</p>
                </CardContent>
              </Card>

              <Card className="premium-card">
                <CardHeader className="pb-3">
                  <CardTitle className="text-lg flex items-center">
                    <Search className="w-5 h-5 mr-2 text-green-600" />
                    Verified
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-3xl font-bold text-green-600">
                    {certificates.length}
                  </div>
                  <p className="text-sm text-gray-600 mt-1">Active in database</p>
                </CardContent>
              </Card>

              <Card className="premium-card">
                <CardHeader className="pb-3">
                  <CardTitle className="text-lg flex items-center">
                    <Users className="w-5 h-5 mr-2 text-purple-600" />
                    Recent Issues
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-3xl font-bold text-purple-600">
                    {certificates.filter(cert => {
                      const issueDate = new Date(cert.issueDate);
                      const thirtyDaysAgo = new Date();
                      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
                      return issueDate > thirtyDaysAgo;
                    }).length}
                  </div>
                  <p className="text-sm text-gray-600 mt-1">Last 30 days</p>
                </CardContent>
              </Card>
            </div>

            {/* Certificates List */}
            <Card className="premium-card">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Shield className="w-5 h-5 text-primary" />
                  <span>Certificate Database Management</span>
                </CardTitle>
                <p className="text-sm text-gray-600">
                  View and manage all GIE laboratory issued certificates
                </p>
              </CardHeader>
              <CardContent>
                {certificatesLoading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                    <p>Loading certificate database...</p>
                  </div>
                ) : certificates.length === 0 ? (
                  <div className="text-center py-8 text-gray-500">
                    <FileText className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                    <p>No certificates in database</p>
                    <p className="text-sm">Issue your first certificate to get started</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {certificates.map((certificate: Certificate) => (
                      <div key={certificate.id} className="border rounded-lg p-6 hover:bg-gray-50 transition-colors premium-card">
                        <div className="flex justify-between items-start">
                          <div>
                            <h3 className="font-bold text-xl text-primary">
                              {certificate.certificateNumber}
                            </h3>
                            <p className="text-gray-900 font-semibold text-lg">
                              {certificate.gemstoneType} - {certificate.weight}
                            </p>
                            <div className="flex items-center space-x-6 mt-3 text-sm text-gray-600">
                              <span><strong>Color:</strong> {certificate.color}</span>
                              <span><strong>Clarity:</strong> {certificate.clarity}</span>
                              <span><strong>Cut:</strong> {certificate.cut}</span>
                            </div>
                            {certificate.origin && (
                              <div className="mt-2 text-sm text-gray-600">
                                <strong>Origin:</strong> {certificate.origin}
                              </div>
                            )}
                          </div>
                          <div className="text-right">
                            <Badge className="bg-primary text-white mb-2">
                              {certificate.issueDate}
                            </Badge>
                            <p className="text-xs text-gray-500">
                              GIE Certified
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="add-certificate">
            <Card className="premium-card">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Plus className="w-5 h-5 text-primary" />
                  <span>Issue New GIE Certificate</span>
                </CardTitle>
                <p className="text-sm text-gray-600">
                  Create and issue a new gemstone certificate with GIE laboratory authentication
                </p>
              </CardHeader>
              <CardContent>
                <CertificateForm onSuccess={handleCertificateCreated} />
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

