import express from "express";
import { createServer } from "http";
import { storage } from "./storage.js";
import { insertCertificateSchema } from "../shared/schema.js";
import multer from "multer";
import path from "path";
import { promises as fs } from "fs";

export async function registerRoutes(app) {
  // Configure multer for file uploads
  const upload = multer({
    dest: "uploads/",
    limits: {
      fileSize: 10 * 1024 * 1024, // 10MB limit
    },
    fileFilter: (req, file, cb) => {
      if (file.mimetype.startsWith('image/')) {
        cb(null, true);
      } else {
        cb(new Error('Only image files are allowed'));
      }
    }
  });

  // Ensure uploads directory exists
  try {
    await fs.mkdir("uploads", { recursive: true });
  } catch (error) {
    console.log("Uploads directory already exists or couldn't be created");
  }

  // Authentication middleware - disabled for simple upload form
  const requireAuth = (req, res, next) => {
    next(); // No authentication required
  };

  // Admin login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Username and password required" });
      }

      const user = await storage.getUserByUsername(username);
      if (!user || user.password !== password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.userId = user.id;
      res.json({ message: "Login successful", user: { id: user.id, username: user.username } });
    } catch (error) {
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Admin logout
  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  // Check auth status
  app.get("/api/auth/me", async (req, res) => {
    if (!req.session?.userId) {
      return res.status(401).json({ message: "Not authenticated" });
    }

    const user = await storage.getUser(req.session.userId);
    if (!user) {
      return res.status(401).json({ message: "User not found" });
    }

    res.json({ user: { id: user.id, username: user.username } });
  });

  // Image upload endpoint
  app.post("/api/upload-image", upload.single("image"), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No image file provided" });
      }

      // Create a unique filename with original extension
      const timestamp = Date.now();
      const originalExtension = path.extname(req.file.originalname);
      const newFilename = `gemstone_${timestamp}${originalExtension}`;
      const newPath = path.join("uploads", newFilename);

      // Move file to new location with proper name
      await fs.rename(req.file.path, newPath);

      // Return the image URL that can be served statically
      const imageUrl = `/uploads/${newFilename}`;
      res.json({ imageUrl });
    } catch (error) {
      console.error("Image upload error:", error);
      res.status(500).json({ message: "Image upload failed" });
    }
  });

  // Serve uploaded images
  app.use("/uploads", express.static("uploads"));

  // Public certificate lookup
  app.get("/api/certificates/lookup/:certificateNumber", async (req, res) => {
    try {
      const { certificateNumber } = req.params;

      if (!certificateNumber) {
        return res.status(400).json({ message: "Certificate number is required" });
      }

      const certificate = await storage.getCertificateByNumber(certificateNumber);
      if (!certificate) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json(certificate);
    } catch (error) {
      res.status(500).json({ message: "Search failed" });
    }
  });

  // Admin routes - require authentication
  app.get("/api/certificates", requireAuth, async (req, res) => {
    try {
      const certificates = await storage.getAllCertificates();
      res.json(certificates);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch certificates" });
    }
  });

  // Public certificate upload endpoint (for direct admin access)
  app.post("/api/certificates/upload", async (req, res) => {
    try {
      const validatedData = insertCertificateSchema.parse(req.body);

      // Check if certificate number already exists
      const existing = await storage.getCertificateByNumber(validatedData.certificateNumber);
      if (existing) {
        return res.status(400).json({ message: "Certificate number already exists" });
      }

      const certificate = await storage.createCertificate(validatedData);
      res.status(201).json(certificate);
    } catch (error) {
      if (error.errors) {
        return res.status(400).json({ message: "Validation failed", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create certificate" });
    }
  });

  app.post("/api/certificates", requireAuth, async (req, res) => {
    try {
      const validatedData = insertCertificateSchema.parse(req.body);

      // Check if certificate number already exists
      const existing = await storage.getCertificateByNumber(validatedData.certificateNumber);
      if (existing) {
        return res.status(400).json({ message: "Certificate number already exists" });
      }

      const certificate = await storage.createCertificate(validatedData);
      res.status(201).json(certificate);
    } catch (error) {
      if (error.errors) {
        return res.status(400).json({ message: "Validation failed", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create certificate" });
    }
  });

  app.put("/api/certificates/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertCertificateSchema.partial().parse(req.body);

      const certificate = await storage.updateCertificate(id, validatedData);
      if (!certificate) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json(certificate);
    } catch (error) {
      if (error.errors) {
        return res.status(400).json({ message: "Validation failed", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update certificate" });
    }
  });

  app.delete("/api/certificates/:id", requireAuth, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteCertificate(id);

      if (!deleted) {
        return res.status(404).json({ message: "Certificate not found" });
      }

      res.json({ message: "Certificate deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete certificate" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

