import { db } from './db.js';
import { certificates } from '../shared/schema.js';
import { eq } from 'drizzle-orm';

// Interface for storage operations
export class IStorage {
  async getCertificate(certificateNumber) {
    throw new Error('Not implemented');
  }

  async createCertificate(certificateData) {
    throw new Error('Not implemented');
  }

  async checkCertificateExists(certificateNumber) {
    throw new Error('Not implemented');
  }
}

// Database storage implementation
export class DatabaseStorage extends IStorage {
  async getCertificate(certificateNumber) {
    try {
      const result = await db
        .select()
        .from(certificates)
        .where(eq(certificates.certificateNumber, certificateNumber))
        .limit(1);

      return result[0] || null;
    } catch (error) {
      console.error('Error getting certificate:', error);
      return null;
    }
  }

  async createCertificate(certificateData) {
    try {
      const newCertificate = {
        id: crypto.randomUUID(),
        certificateNumber: certificateData.certificateNumber,
        gemstoneType: certificateData.gemstoneType,
        caratWeight: certificateData.caratWeight,
        cut: certificateData.cut,
        measurements: certificateData.measurements,
        clarity: certificateData.clarity,
        color: certificateData.color,
        polish: certificateData.polish,
        origin: certificateData.origin,
        imageUrl: certificateData.imageUrl || null,
        issueDate: new Date(),
        createdAt: new Date()
      };

      const result = await db
        .insert(certificates)
        .values(newCertificate)
        .returning();

      return result[0];
    } catch (error) {
      console.error('Error creating certificate:', error);
      throw error;
    }
  }

  async checkCertificateExists(certificateNumber) {
    try {
      const result = await db
        .select({ id: certificates.id })
        .from(certificates)
        .where(eq(certificates.certificateNumber, certificateNumber))
        .limit(1);

      return result.length > 0;
    } catch (error) {
      console.error('Error checking certificate exists:', error);
      return false;
    }
  }
}

export const storage = new DatabaseStorage();

server/vite.js.content:
import express from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import viteConfig from "../vite.config.js";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app, server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.jsx"`,
        `src="/src/main.jsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e);
      next(e);
    }
  });
}

export function serveStatic(app) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

