import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import CertificateDisplay from "@/components/certificate-display";

export default function CertificateSearch() {
  const [certificateNumber, setCertificateNumber] = useState("");
  const [shouldSearch, setShouldSearch] = useState(false);
  const { toast } = useToast();

  const { data: certificate, isLoading, error } = useQuery({
    queryKey: ["/api/certificates/lookup", certificateNumber],
    enabled: shouldSearch && certificateNumber.trim() !== "",
    retry: false,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!certificateNumber.trim()) {
      toast({
        title: "Error",
        description: "Please enter a certificate number",
        variant: "destructive",
      });
      return;
    }
    setShouldSearch(true);
  };

  const handleReset = () => {
    setCertificateNumber("");
    setShouldSearch(false);
  };

  return (
    <div className="space-y-8">
      {/* Search Form */}
      <div className="max-w-2xl mx-auto">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <Input
                type="text"
                id="certificateNumber"
                value={certificateNumber}
                onChange={(e) => setCertificateNumber(e.target.value)}
                placeholder="Enter Certificate Number (e.g., GIE-2024-001234)"
                className="h-14 px-6 text-lg border-2 border-gray-300 focus:border-primary rounded-lg"
              />
            </div>
            <Button 
              type="submit" 
              className="h-14 px-8 bg-primary hover:bg-primary/90 text-white font-semibold rounded-lg pulse-glow"
              disabled={isLoading}
            >
              {isLoading ? "Searching..." : "Track Report"}
            </Button>
          </div>
          {shouldSearch && (
            <div className="text-center">
              <Button type="button" variant="outline" onClick={handleReset} className="px-6">
                Clear Search
              </Button>
            </div>
          )}
        </form>
      </div>

      {/* Search Results */}
      {error && (
        <div className="text-center">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
            <p className="text-red-600 font-medium">Certificate not found</p>
            <p className="text-red-500 text-sm mt-2">
              Please check the certificate number and try again.
            </p>
          </div>
        </div>
      )}

      {certificate && (
        <div className="max-w-4xl mx-auto">
          <CertificateDisplay certificate={certificate} />
        </div>
      )}
    </div>
  );
}

