  clarity: z.string().min(1, "Clarity is required"),
  cut: z.string().min(1, "Cut is required"),
  origin: z.string().min(1, "Origin is required"),
  issueDate: z.string().min(1, "Issue date is required"),
  description: z.string().optional(),
  dimensions: z.string().optional(),
});

export default function UploadCertificate() {
  const [imageFile, setImageFile] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const { toast } = useToast();

  const form = useForm({
    resolver: zodResolver(publicCertificateSchema),
    defaultValues: {
      certificateNumber: "",
      gemstoneType: "",
      weight: "",
      color: "",
      clarity: "",
      cut: "",
      origin: "",
      issueDate: new Date().toISOString().split('T')[0],
      description: "",
      dimensions: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: (data) => 
      apiRequest("POST", "/api/certificates/upload", data),
    onSuccess: () => {
      setSubmitted(true);
      toast({
        title: "Certificate Created",
        description: "Certificate has been successfully created and is now available for public lookup.",
      });
      form.reset();
      setImageFile(null);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create certificate",
        variant: "destructive",
      });
    },
  });

  const uploadImageMutation = useMutation({
    mutationFn: (formData) => {
      return fetch("/api/upload-image", {
        method: "POST",
        body: formData,
        credentials: "include",
      }).then(res => {
        if (!res.ok) throw new Error("Upload failed");
        return res.json();
      });
    },
    onSuccess: (data) => {
      form.setValue("imageUrl", data.imageUrl);
      toast({
        title: "Image Uploaded",
        description: "Gemstone image has been uploaded successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to upload image",
        variant: "destructive",
      });
    },
  });

  const handleImageUpload = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      setImageFile(file);
      const formData = new FormData();
      formData.append("image", file);
      uploadImageMutation.mutate(formData);
    }
  };

  const onSubmit = (data) => {
    const certificateData = {
      ...data,
      measurements: data.dimensions || "",
      polish: "Excellent",
      symmetry: "Excellent",
      fluorescence: "None",
      imageUrl: form.watch("imageUrl") || "",
    };
    createMutation.mutate(certificateData);
  };

  if (submitted) {
    return (
      <main className="min-h-screen bg-gray-50">
        <Navigation />
        <div className="max-w-2xl mx-auto px-4 py-16">
          <Card className="text-center">
            <CardContent className="pt-12 pb-8">
              <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-6" />
              <h2 className="text-2xl font-bold text-gray-900 mb-4">Certificate Created Successfully!</h2>
              <p className="text-gray-600 mb-8">
                Your certificate has been created and is now available for public verification.
              </p>
              <Button 
                onClick={() => setSubmitted(false)}
                className="mr-4"
              >
                Create Another Certificate
              </Button>
              <Button 
                variant="outline"
                onClick={() => window.location.href = "/"}
              >
                Back to Home
              </Button>
            </CardContent>
          </Card>
        </div>
      </main>
    );
  }

  return (
    <main className="min-h-screen bg-gray-50">
      <Navigation />

      {/* Header */}
      <div className="bg-white border-b">
        <div className="max-w-4xl mx-auto px-4 py-8">
          <div className="flex items-center space-x-4">
            <img src={gieLogoPath} alt="GIE Logo" className="h-12 w-12" />
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Create GIE Certificate</h1>
              <p className="text-gray-600">Issue a new gemstone identification certificate</p>
            </div>
          </div>
        </div>
      </div>

      {/* Form */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        <Card>
          <CardHeader>
            <CardTitle className="text-primary flex items-center space-x-2">
              <Shield className="h-6 w-6" />
              <span>Certificate Information</span>
            </CardTitle>
            <p className="text-gray-600">Enter the gemstone details and certification information</p>
          </CardHeader>
          <CardContent>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
              {/* Basic Information */}
              <div className="grid md:grid-cols-2 gap-6">
                <div className="space-y-4">
                  <h4 className="font-semibold text-gray-900 border-b pb-2">Basic Information</h4>

                  <div>
                    <Label htmlFor="certificateNumber">Certificate Number *</Label>
                    <Input
                      id="certificateNumber"
                      {...form.register("certificateNumber")}
                      placeholder="e.g., GIE-2024-001234"
                      className="mt-1"
                    />
                    {form.formState.errors.certificateNumber && (
                      <p className="text-red-600 text-sm mt-1">
                        {form.formState.errors.certificateNumber.message}
                      </p>
                    )}
                  </div>

                  <div>
                    <Label htmlFor="gemstoneType">Gemstone Type *</Label>
                    <Input
                      id="gemstoneType"
                      {...form.register("gemstoneType")}
                      placeholder="e.g., Natural Diamond, Ruby, Sapphire"
                      className="mt-1"
                    />
                    {form.formState.errors.gemstoneType && (
                      <p className="text-red-600 text-sm mt-1">
                        {form.formState.errors.gemstoneType.message}
                      </p>
                    )}
                  </div>

                  <div>
                    <Label htmlFor="weight">Weight *</Label>
                    <Input
                      id="weight"
                      {...form.register("weight")}
                      placeholder="e.g., 2.15 Carats"
                      className="mt-1"
                    />
                    {form.formState.errors.weight && (
                      <p className="text-red-600 text-sm mt-1">
                        {form.formState.errors.weight.message}
                      </p>
                    )}
                  </div>

                  <div>
                    <Label htmlFor="issueDate">Issue Date *</Label>
                    <Input
                      id="issueDate"
                      type="date"
                      {...form.register("issueDate")}
                      className="mt-1"
                    />
                  </div>
                </div>

                <div className="space-y-4">
                  <h4 className="font-semibold text-gray-900 border-b pb-2">Gemstone Properties</h4>

                  <div>
                    <Label htmlFor="color">Color *</Label>
                    <Input
                      id="color"
                      {...form.register("color")}
                      placeholder="e.g., D, E, F or Red, Blue"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <Label htmlFor="clarity">Clarity *</Label>
                    <Input
                      id="clarity"
                      {...form.register("clarity")}
                      placeholder="e.g., VVS1, VVS2, VS1, VS2"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <Label htmlFor="cut">Cut *</Label>
                    <Input
                      id="cut"
                      {...form.register("cut")}
                      placeholder="e.g., Excellent, Round, Oval"
                      className="mt-1"
                    />
                  </div>

                  <div>
                    <Label htmlFor="origin">Origin *</Label>
                    <Input
                      id="origin"
                      {...form.register("origin")}
                      placeholder="e.g., Botswana, Myanmar, Kashmir"
                      className="mt-1"
                    />
                  </div>
                </div>
              </div>

              {/* Additional Information */}
              <div className="space-y-4">
                <h4 className="font-semibold text-gray-900 border-b pb-2">Additional Information</h4>

                <div>
                  <Label htmlFor="dimensions">Measurements</Label>
                  <Input
                    id="dimensions"
                    {...form.register("dimensions")}
                    placeholder="e.g., 8.52 x 8.48 x 5.23 mm"
                    className="mt-1"
                  />
                </div>

                <div>
                  <Label htmlFor="description">Additional Notes</Label>
                  <Textarea
                    id="description"
                    {...form.register("description")}
                    placeholder="Any additional certification notes or observations..."
                    className="mt-1"
                    rows={3}
                  />
                </div>
              </div>

              {/* Image Upload */}
              <div className="space-y-4">
                <h4 className="font-semibold text-gray-900 border-b pb-2">Gemstone Image</h4>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    className="hidden"
                    id="image-upload"
                  />
                  <label htmlFor="image-upload" className="cursor-pointer">
                    <FileImage className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-600 mb-2">Click to upload gemstone image</p>
                    <p className="text-sm text-gray-500">PNG, JPG up to 10MB</p>
                  </label>
                  {imageFile && (
                    <p className="text-green-600 mt-2">✓ {imageFile.name}</p>
                  )}
                </div>
              </div>

              {/* Submit Button */}
              <div className="flex justify-end space-x-4 pt-6 border-t">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => window.location.href = "/"}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createMutation.isPending}
                  className="bg-primary hover:bg-primary/90"
                >
                  {createMutation.isPending ? (
                    <>
                      <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                      Creating Certificate...
                    </>
                  ) : (
                    <>
                      <Upload className="h-4 w-4 mr-2" />
                      Create Certificate
                    </>
                  )}
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    </main>
  );
}

